    FROM   sci_opp_loi_duedate_detail
    WHERE   OPPORTUNITY_ID = V_ID AND DATE_TYPE = 1;
   
    SELECT   opportunity_id,
               (CASE
                   WHEN loi_due_date IS NULL THEN (DATE_REMARKS)
