               sl_dtl.scival_id,
               sl_dtl.column_desc,
               sl_dtl.column_id,
               (SELECT   column_name
                  FROM   sci_column_master
                 WHERE   column_id = sl_dtl.column_id)
                  column_name,
               sl_dtl.moduleid,
               (SELECT   modulename
                  FROM   sci_modules
                 WHERE   moduleid = sl_dtl.moduleid)
                  modulename,
               sl_mast.language_id,
               sl_mast.language_group_id,
               sl_mast.language_name,
               sl_mast.language_code,
