   SELECT   ID, MODULEID
     INTO   V_ID, V_MODULEID
     FROM   SCI_WORKFLOW
    WHERE   WORKFLOWID = P_WORKFLOWID;

 SELECT   COUNT(1)
     INTO   V_INDEXCHK
     FROM   (SELECT   TRIM(LEADING CHAR (9 USING ASCII) FROM TRIM (INDEXEDNAME)) INDEXEDNAME
               FROM   AWARDEENAME
              WHERE   AWARDEE_ID IN
                            (SELECT   AWARDEE_ID
                               FROM   AWARDEENAME
