                                                            (SELECT
      AWARDEES_ID
                                                               FROM   AWARDEES
                                                              WHERE   AWARD_ID
      =
                                                                         V_ID)
      ))
                      AND INDEXEDNAME LIKE '%' or CONCAT(IFNULL(P_INDEXEDNAME, '') , '%')) T
    WHERE   INDEXEDNAME = P_INDEXEDNAME;

   IF V_INDEXCHK  <= 0
   THEN
      SELECT   COUNT(*)
        INTO   l_awrdcount
        FROM   AWARDEES
       WHERE   AWARD_ID = V_ID;

      IF l_awrdcount > 0
      THEN
