         DELETE FROM   fax
               WHERE   AFFILIATION_ID IN
                             (SELECT   AFFILIATION_ID
                                FROM   AFFILIATION
                               WHERE   AWARDEEINSTITUTION_ID IN
                                             (SELECT   AWARDEEINSTITUTION_ID
                                                FROM   AWARDEEINSTITUTION
                                               WHERE   AWARDEE_ID =
                                                          p_AWARDEE_ID));
         DELETE FROM   address
               WHERE   AFFILIATION_ID IN
