            UPDATE   amount
            SET   CURRENCY = p_currency, AMOUNT_TEXT = p_amount
          WHERE   AWARDEE_ID = p_AWARDEE_ID;
           

         IF p_type = 'institution'
         THEN
            SELECT   COUNT(*)
              INTO   V_INSCOUNT
              FROM   awardeeinstitution
             WHERE   awardee_id = p_AWARDEE_ID;

