                              (SELECT   COUNT (1)
                                 FROM   AWARDEEINSTITUTION
                                WHERE   AWARDEE_ID = B.AWARDEE_ID)
                                 FLAG
                       FROM   AWARDEES A,
                              AWARDEE B,
                              AWARDEENAME C,
                              SCI_AWARDEETYPETYPE d,  amount am
