        FROM   sci_opp_loi_duedate_detail
       WHERE   OPPORTUNITY_ID = V_ID AND DATE_TYPE = 8;
   
      SELECT   OPPORTUNITYSTATUS
        FROM   opportunity
       WHERE   OPPORTUNITY_ID = V_ID;       
