                  P_UNDERGRADUATE,
                  L_INDIVIDUALELIGIBILITY_ID_SEQ,
                  L_ELIGCLASSIFICATION_SEQ);

       INSERT INTO CITIZENSHIP (NORESTRICTION,
                                  COUNTRY,
                                  CITIZENSHIP_TEXT,
                                  INDIVIDUALELIGIBILITY_ID)
           VALUES   (P_NORESTRICTION,
                     P_COUNTRY,
                     P_CITIZENSHIP_TEXT,
                     L_INDIVIDUALELIGIBILITY_ID_SEQ);

