                     AND it.RELATEDITEMS_ID = ch.RELATEDITEMS_ID
                     AND ch.OPPORTUNITY_ID = v_fundingbodyid;
      ELSEIF p_mode = 5
      THEN
            SELECT   it.RELTYPE,
                     it.description,  
