             WHERE   it.instruction_ID = i.instruction_ID
                     AND i.OPPORTUNITY_ID = o.OPPORTUNITY_ID
                     AND l.item_id = it.item_id
                     AND i.OPPORTUNITY_ID = v_fundingbodyid;
                     
                      ELSEIF p_mode = 15
      THEN
            SELECT   it.RELTYPE,
                     it.description,
                     it.ITEM_ID,
                     it.lang,
                     it.RELATEDITEMS_ID id,
                     it.licenseInformation_id,
