      END IF;

       -- SET l_tran_id = sci_language_dtl_tran_id_seq.NEXTVAL;
       -- select tran_id+1 into l_tran_id from sci_language_detail;
	   select tran_id+1 into l_tran_id from sci_language_detail order by tran_id desc limit 0,1;
      
