        FROM   `CHANGE` WHERE   CHANGEHISTORY_ID = v_CHANGEHISTORY_ID AND version = (SELECT   MAX(version) FROM   `CHANGE` WHERE   CHANGEHISTORY_ID = v_CHANGEHISTORY_ID);

		SELECT   POSTDATE_IS_SAVE 
		FROM   `CHANGE` WHERE   CHANGEHISTORY_ID = v_CHANGEHISTORY_ID AND version = (SELECT   MAX(version) FROM   `CHANGE` WHERE   CHANGEHISTORY_ID = v_CHANGEHISTORY_ID);
   END IF;
