            DELETE FROM   amount
                  WHERE       CURRENCY = p_CURRENCY
                          AND AMOUNT_TEXT = lv_amount
                          AND AWARD_ID = v_ID;

         END IF;


            SELECT   *
              FROM   amount
             WHERE   award_ID = V_ID;

        SELECT   NULL URL, NULL LINK_TEXT FROM DUAL;
      ELSEIF PAGE_MODE = 5
      -- - started on 4 jun-2018 by avinash  for installment and amount
      THEN
         IF p_mode = 0
         THEN
            SELECT   COUNT( * )
              INTO   v_count
              FROM   amount
