                                        SEQUENCE_ID)
                 VALUES   (l_inst_Amount_Id_seq,
                           p_startdate,
                           p_enddate,
                           p_amount,
                           p_currency,
                           l_INST_AMOUNT_l_SEQUENCE_ID);

               INSERT INTO totalAmount (
                                           INSTALLMENTANDAMOUNT_ID,
                                           AMOUNT,
