               AND sl_dtl.column_id = 5
               AND sl_dtl.scival_id = v_id;

      SELECT   STATUS, REVISIONHISTORY_ID, OPPORTUNITY_ID
        FROM   revisionhistory
       WHERE   opportunity_id = v_id;


      SELECT   VERSION,
                  CONCAT(IFNULL(DATE_FORMAT (reviseddate_text, '%Y-%m-%d'), '')
