               revisionhistory_id
        FROM   createddate
       WHERE   revisionhistory_id IN (SELECT   revisionhistory_id
                                        FROM   revisionhistory
                                       WHERE   OPPORTUNITY_ID = v_id);

   
      SELECT   ID, TYPE_TEXT, OPPORTUNITY_ID
        FROM   TYPE
       WHERE   opportunity_id = v_id;

     SELECT   ELIGIBILITYCLASSIFICATION_ID, OPPORTUNITY_ID
