       WHERE       ecls.REGION_SPECIFIC_ID = eclsrs.ID
               AND ELIGIBILITYCLASSIFICATION_ID IS NOT NULL
               AND opportunity_id = v_id;

    SELECT   CURRENCY, ESTIMATEDFUNDING_TEXT, OPPORTUNITY_ID
