               item.RELATEDITEMS_ID,
               item.SYNOPSIS_ID,
               item.DESCRIPTION
   FROM      item
               LEFT JOIN
                  link lnk
               ON lnk.item_id = item.item_id
       WHERE   SYNOPSIS_ID IN (SELECT   SYNOPSIS_ID
                                 FROM   synopsis
                                WHERE   opportunity_id = v_id);

      SELECT   currency,
