      UNION ALL      
      SELECT   NULL RELTYPE,
               CASE WHEN LANG IS NULL THEN 'en' ELSE lang END lang,
               ITEM_ID,
               RELATEDITEMS_ID,
               SYNOPSIS_ID,
               DESCRIPTION,
               SUBJECTMATTER_ID,
               ELIGIBILITYDESCRIPTION_ID,
               ESTIMATEDAMOUNTDESCRIPTION_ID,
               LIMITEDSUBMISSIONDESC_ID
        FROM   item
       WHERE   SYNOPSIS_ID IN (SELECT   SYNOPSIS_ID
                                 FROM   synopsis
                                WHERE   opportunity_id = v_id)
      UNION ALL
      SELECT   RELTYPE,
