   SELECT   TYPE ,
               DATE_FORMAT (STR_TO_DATE (POSTDATE, '%d-%m-%Y'), '%d-%m-%Y')
                  POSTDATE,
               VERSION,
               CHANGE_TEXT,
               CHANGEHISTORY_ID
                FROM   X -- change by neha
       WHERE   CHANGEHISTORY_ID IN (SELECT   changehistory_id
                                      FROM   changehistory
                                     WHERE   opportunity_id = v_id)
               AND version =
