      SELECT   lnk.url,
               CASE WHEN item.LANG IS NULL THEN 'en' ELSE lang END lang,
               item.ITEM_ID,
               item.RELATEDITEMS_ID,
               item.DESCRIPTION,
               INSTRUCTION_ID
        FROM      item
