       WHERE   INSTRUCTION_ID IN (SELECT   INSTRUCTION_ID
                                    FROM   INSTRUCTION
                                   WHERE   opportunity_id = v_id);

   OPEN p_relatedTo FOR
      SELECT   relid,
               ort.relation_name,
               RELATED_OPP_ID,
               sro.OPPORTUNITYNAME,
               sld.LANGUAGE_ID,
               slm.language_code,
               sro.description
