	P_ID             bigint,
	P_moduleid       INTEGER,
	P_taskid         INTEGER,
	P_CYCLE          INTEGER
)
SP: BEGIN
	IF P_taskid = 7
	THEN
		DELETE FROM sci_workflow WHERE moduleid = P_moduleid AND id = P_ID AND taskid = 7;
		COMMIT;
		LEAVE SP;
	END IF;
   
	IF P_moduleid = 2
