	P_moduleid      INTEGER,
	P_taskid        INTEGER,
	P_CYCLE         INTEGER,
	P_ALLOCATION    INTEGER
)
BEGIN
	DECLARE lCount	INTEGER;
    
    SELECT COUNT(1) INTO lCount FROM userassignment WHERE userid = p_userid AND moduleid = p_moduleid; 
    
    IF P_taskid = 1
	THEN
		IF P_ALLOCATION = 0
